import React, { useEffect } from "react";
import { useLocation, useNavigate, useParams } from "react-router-dom";
import { Query } from "react-apollo";
import { useReactiveVar } from "@apollo/client";
import movieVar, { clickedmovieVar, getMovie, Movie } from "./stores/movies";
const Detail = () => {
  const params = useParams();
  const movie = useReactiveVar(movieVar);
  const moveId = params.moveId as unknown as number;
  getMovie(moveId);
  const nowMovie = useReactiveVar(clickedmovieVar);

  return (
    <>
      {params.moveId}
      {nowMovie && (
        <>
          <img src={movie.medium_cover_image} />
          <h2 key={movie.id}>
            {movie.title}/ {movie.rating}
          </h2>
        </>
      )}
      {/* {movie &&
        movie.map((temp: Movie) => {
          if (temp.id! == moveId) return <h2> {temp.title} </h2>;
        })} */}
    </>
  );
};
export default Detail;
